# Basic if statement
x <- 10

if (x > 5) {
  print("x is greater than 5")
}## [1] "x is greater than 5"
# If-else
x <- 3

if (x > 5) {
  print("x is greater than 5")
} else {
  print("x is less than or equal to 5")
}## [1] "x is less than or equal to 5"
# If-else if-else chain
score <- 75

if (score >= 90) {
  grade <- "A"
} else if (score >= 80) {
  grade <- "B"
} else if (score >= 70) {
  grade <- "C"
} else {
  grade <- "F"
}

grade## [1] "C"

# Basic function structure
square <- function(x) {
  result <- x^2
  return(result)
}

square(4)## [1] 16
square(1:5)## [1]  1  4  9 16 25

# Function with default parameter values
power <- function(base, exponent = 2) {
  result <- base^exponent
  return(result)
}

power(3)      # Uses default exponent = 2## [1] 9
power(3, 3)   # Explicit exponent## [1] 27
power(2, exponent = 4)  # Named argument## [1] 16

